mapper.select:
WHERE CNO IN ( SELECT CNO FROM SPRING_COMMENT WHERE DELETE_FLAG = 'N' AND PARENT_CNO = 0
UNION ALL
SELECT DISTINCT A.CNO FROM SPRING_COMMENT A INNER JOIN SPRING_COMMENT B ON B.PARENT_CNO = A.CNO AND A.PARENT_CNO = 0 WHERE A.DELETE_FLAG = 'Y'AND B.DELETE_FLAG = 'N' ) AND PNO = #{pno}

mapper.insert:
INSERT INTO SPRING_COMMENT ( PNO
                           , PARENT_CNO
                           , RECEIVER
                           , CONTENT
                           , WRITER
                           , PASSWORD)
VALUES ( #{pno}
       , #{parent_cno}
       , #{receiver}
       , #{content}
       , #{writer}
       , #{password})
